package controlStatements;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int num = 10;
        if(num>0)
        {
        	System.out.println("Number is greater than zero");
        }
        else
        {
        	System.out.println("Number is less than zero");
        }
	}

}
