package operators;

public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a = 10;
        System.out.println("=====Incrementing=====");
        System.out.println(a);
        System.out.println(a++);
        System.out.println(a);
        System.out.println(++a);
        System.out.println(a);
        System.out.println("====Decrementing=====");
        int b = 10;
        System.out.println(b);
        System.out.println(b--);
        System.out.println(b);
        System.out.println(--b);
        
	}

}
